CREATE TABLE `hosp_farma`.`pacientes` ( `id` INT(11) NOT NULL AUTO_INCREMENT , `nombre` VARCHAR(100) NOT NULL , `a_paterno` VARCHAR(100) NOT NULL , `a_materno` VARCHAR(100) NOT NULL , `num_expediente` INT(10) NOT NULL , `sexo` VARCHAR(14) NOT NULL , `peso` FLOAT(5) NOT NULL , `estatura` FLOAT(5) NOT NULL , `imc` FLOAT(5) NOT NULL , `reg_creacion` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = InnoDB;